import Vue from "vue";
import VueI18n from "vue-i18n";
import tr from "./tr.json";
import en from "./en.json";
var defaultLocale = $("#app").attr("locale");
var languages = {
  tr: tr,
  en: en
};
var messages = Object.assign(languages);
Vue.use(VueI18n);
var i18n = new VueI18n({
  locale: defaultLocale,
  fallbackLocale: "en",
  messages: messages,
  preserveDirectiveContent: true,
  silentTranslationWarn: true
});
export default i18n;
