import Vue from 'vue';
import { ContextMenuPlugin } from "@syncfusion/ej2-vue-navigations";
Vue.use(ContextMenuPlugin);
export default {
  data() {
    return {
      viewMenu: false,
      top: '0px',
      left: '0px',
      menuItems: [{
        text: 'Cut'
      }, {
        text: 'Copy'
      }, {
        text: 'Paste'
      }]
    };
  },

  methods: {
    testFunc($event) {
      console.dir($event);
      this.viewMenu = true;
      let elem = document.querySelector("#right-click-menu");
      elem.pageX = $event.pageX;
      elem.pageY = $event.pageY;
    },

    printname() {
      var giveName = document.getElementById("sonucMetin").value; //console.log(giveName);

      $('#mehmet1').text(giveName);
    },

    printname2() {
      //var giveName = document.getElementById("sonucMetin").value;
      //console.log(giveName);
      $('#mehmet1').text("giveNameeeeeeeee");
    },

    setMenu: function (top, left) {
      largestHeight = window.innerHeight - this.$$.right.offsetHeight - 25;
      largestWidth = window.innerWidth - this.$$.right.offsetWidth - 25;
      if (top > largestHeight) top = largestHeight;
      if (left > largestWidth) left = largestWidth;
      this.top = top + 'px';
      this.left = left + 'px';
    },
    closeMenu: function () {
      this.viewMenu = false;
    },
    openMenu: function (e) {
      this.viewMenu = true;
      Vue.nextTick(function () {
        this.$$.right.focus();
        this.setMenu(e.y, e.x);
      }.bind(this));
      e.preventDefault();
    }
  }
};
