export default {
  name: 'CmpContextMenu',
  props: {
    display: Boolean // prop detect if we should show context menu

  },
  data: function data() {
    return {
      left: 0,
      // left position
      top: 0,
      // top position
      show: false // affect display of context menu

    };
  },
  computed: {
    // get position of context menu
    style: function style() {
      return {
        top: this.top + 'px',
        left: this.left + 'px'
      };
    }
  },
  methods: {
    printname: function printname() {
      var giveName = document.getElementById("sonucMetin").value; //console.log(giveName);

      $('#mehmet1').text(giveName);
    },
    rightClick: function rightClick(clickEvent) {
      clickEvent.preventDefault(); // return false;
    }
  },
  // closes context menu 
  close: function close() {
    this.show = false;
    this.left = 0;
    this.top = 0;
  },
  open: function open(evt) {
    var _this = this;

    // updates position of context menu 
    this.left = evt.pageX || evt.clientX;
    this.top = evt.pageY || evt.clientY; // make element focused 
    // @ts-ignore

    Vue.nextTick(function () {
      return _this.$el.focus();
    });
    this.show = true;
  }
};
